<script>
export default {
        data () {
            return {
                seconds: 60,
                minutes: 59,
                date2: new Date(),
                ndate2: new Date(),
                open:true
            }
        },
        mounted(){
            this.ndate2 = this.changeTimezone(this.date2, "Europe/Berlin");
            this.minutes = 60 - this.ndate2.getMinutes();
            this.seconds = 60 - this.ndate2.getSeconds();
            
            // if(localStorage.getItem('seconds') !== null){
            //     this.seconds = parseInt(localStorage.getItem('seconds'))
            // }
            // if(localStorage.getItem('minutes') !== null){
            //     this.minutes = parseInt(localStorage.getItem('minutes'))
            // }
        },
        methods: {
            countDownTimer () {
                if (this.seconds > 0) {
                    setTimeout(() => {
                        this.seconds -= 1
                        const nvdate = new Date()
                        const tnvdate = this.changeTimezone(nvdate,"Europe/Berlin");
                        this.minutes = 60 - tnvdate.getMinutes();
                        this.seconds = 60 - tnvdate.getSeconds();
                        this.countDownTimer()
                    }, 1000)
                }
                if (this.seconds == 0) {
                    this.minutes -= 1
                    this.seconds = 60
                    this.countDownTimer()
                }

                if (this.minutes == 0) {
                    this.minutes = 59
                    this.seconds = 60
                    this.countDownTimer()
                }
            },
            getDifferenceInHours(date1, date2) {
            const diffInMs = Math.abs(date2 - date1);
            return diffInMs / (1000 * 60 * 60);
            },
            changeTimezone(date, ianatz) {

                // suppose the date is 12:00 UTC
                var invdate = new Date(date.toLocaleString('en-US', {
                timeZone: ianatz
                }));

                // then invdate will be 07:00 in Toronto
                // and the difference is 5 hours
                var diff = date.getTime() - invdate.getTime();

                // so 12:00 in Toronto is 17:00 UTC
                return new Date(date.getTime() - diff); // needs to substract

                }
        },

        created () {
            this.countDownTimer()
        }
    }
</script>